package laçofor;

public class tabuada {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int cont=1, num=1;
	        
	        for(cont=1; cont<=10; cont++) {
	            for(num=1; num<=10; num++) {
	                System.out.println(cont+"x"+num+" = " +cont*num);
	            }
	        }


	}

}
